import React, { Component, createRef, createContext, useContext, useRef, forwardRef } from 'react'

const Foo = forwardRef(
    (_, inputRef) => {
        // 使用函数组件的Hook,useRef()返回一个虚拟DOM并挂载到ref属性，
        // 和在函数中使用createRef()相同
        const ccc = useRef()
        // const ccc = createRef()
        const onClick = () => {
            ccc.current.focus()
        }
        return (
            <div>
                <input type="text" ref={ccc} />
                <button onClick={onClick}>聚焦</button>
            </div>
        )
    }
)
const App = () => {
    const inputRef = use
    const onClick = () => {
        inputRef.current.focus()
    }
    return (
        <div>
            <Foo ref={inputRef} />
            <button>父组件聚焦</button>
            <button onClick={onClick}>父组件聚焦</button>
        </div>
    )
}
export default App



