import React, { Component, createContext } from 'react'

// 创建一个Context对象
const AppContext = createContext()

// 最外层组件(祖父组件) App 
const App = () => {
    return (
        // 使用AppContext.Provider包裹父组件，并且传值
        <AppContext.Provider value="kobe-bryant">
            <Middle />
        </AppContext.Provider>
    )
}

// 父组件 Middle
const Middle = () => {
    return (<> <Foo /> <Bar /> <Baz /> </>)
}

// 类 孙子 组件 Foo
class Foo extends Component {
    render() {
        return (
            // 使用AppContext.Consumer在类组件中接收值(较老的方式，不是很友好)
            <AppContext.Consumer>
                {
                    value => <div>{value}</div>
                }
            </AppContext.Consumer>
            
        )
    }
}

// 类 孙子 组件 Bar
class Bar extends Component {
    // 在类组价的
    static
    render() {
        return (<div>bar</div>)
    }
}

// 函数 孙子 组件 Baz
const Baz = () => {
    return (<div>baz</div>)
}
