import React,{ useState } from 'react'

export default function Example() {
    const [count, setCount] = useState(0)

    // 相当于 componentDidMount 和 componentDidUp
    return (
        <div>
            <p>你点击了 {count} 次按钮</p>
            <button onClick={() => setCount(count + 1)} > 点我 </button>
        </div>
    )
}

