import React, { Component } from 'react'
// import Demo from './components/1_setState'
// import Demo2 from './components/2_lazyLoad'
// import Demo3 from './components/3_hooks'
// import Demo4 from './components/4_Fragment'
// import Demo5 from './components/5_Context'

import * as echarts from 'echarts'


export default class App extends Component {

    componentDidMount() {
        let myChart = echarts.init(document.getElementById('mycharts'));
        myChart.setOption(
            {
                title: {
                    text: '世界人口总量',
                    subtext: '数据来自网络'
                },
                tooltip: {
                    trigger: 'axis',
                    axisPointer: {
                        type: 'shadow'
                    }
                },
                xAxis: {
                    data: ['衬衫', '羊毛衫', '雪纺衫', '裤子', '高跟鞋', '袜子']
                },
                yAxis: {},
                series: [
                    {
                        name: '销量',
                        type: 'bar',
                        data: [5, 20, 36, 10, 10, 20]
                    }
                ]
            }
        )


        option = {
            title: {
                text: '世界人口总量',
                subtext: '数据来自网络'
            },
            tooltip: {
                trigger: 'axis',
                axisPointer: {
                    type: 'shadow'
                }
            },
            legend: {
                data: ['2011年', '2012年']
            },
            grid: {
                left: '3%',
                right: '4%',
                bottom: '3%',
                containLabel: true
            },
            xAxis: {
                type: 'value',
                boundaryGap: [0, 0.01]
            },
            yAxis: {
                type: 'category',
                data: ['巴西', '印尼', '美国', '印度', '中国', '世界人口(万)']
            },
            series: [
                {
                    name: '2011年',
                    type: 'bar',
                    data: [18203, 23489, 29034, 104970, 131744, 630230]
                },
                {
                    name: '2012年',
                    type: 'bar',
                    data: [19325, 23438, 31000, 121594, 134141, 681807]
                }
            ]
        }


    }

    render() {
        console.log(echarts)
        return (
            <>
                {/* <Demo/> */}
                {/* <Demo2/> */}
                {/* <Demo3/> */}
                {/* <Demo4/> */}
                {/* <Demo5/> */}

                <div className="App">
                    <div id='mycharts' style={{ height: '500px' }}>

                    </div>

                </div>

            </>
        )
    }
}
