import React, { useState, useEffect } from 'react'

const FriendStatus = props => {
    // 调用自定义的HOOK => useExample
    const isOnline = useExample(props.friend.id)

    if (isOnline === null) {
        return 'Loading...'
    }
    return isOnline ? 'Online' : 'Offline'
}

const FriendListItem = props => {
    // 调用自定义的HOOK => useExample
    const isOnline = useExample(props.friend.id)

    return (
        <li style={{}}>
            {props.friend.name}
        </li>
    )
}

