import React, { Component } from 'react'
import './index.css'

export default class Demo5 extends Component {

    state = { userName: 'kobe-bryant' }

    render() {
        return (
            <div className="parent">
                <h3>我是Demo5组件</h3>
                <h4>我的用户名是：{this.state.userName}</h4>
                <A userName={this.state.userName}/>
            </div>
        )
    }
}

class A extends Component {
    render() {
        return (
            <div className="child">
                <h3>我是A组件</h3>
                <B/>
            </div>
        )
    }
}

class B extends Component {
    render() {
        return (
            <div className="grand">
                <h3>我是B组件</h3>
                <h4>我从Demo5组件接收到的用户名是：{this.props.userName}</h4>
            </div>
        )
    }
}
