import React, { Component } from 'react'
import Child from './Child'

export default class Parent extends Component {

    state = {
        hasError: ''
    }

    // 当Parent组件的子组件出现报错则会调用该钩子函数并携带错误信息
    static getDerivedStateFromError(error) {
        console.log(error)
        return {hasError: error}
    }

    render() {
        return (
            <div>
                <h2>我是Parent组件</h2>
                <Child/>
            </div>
        )
    }
}
