import React, { Component, createRef, createContext, useContext } from 'react'
class Foo extends Component {
    ccc = createRef()
    onClick = () => {
        // 这里注意.current拿到的才是inpput节点
        this.ccc.current.focus()
    }
    render() {
        return (
            <div>
                <input type="text" ref={this.ccc} />
                <button onClick={this.onClick}>聚焦</button>
            </div>
        )
    }
}
const App = () => {
    const onClick = () => {
        console.log(ccc)
    }
    return (
        <div>
            <Foo ref={ccc} />
            <button onClick={onClick}>聚焦</button>
        </div>
    )
}
