import React, { Component } from 'react'

export default class Count extends Component {

    state = {count:0}

    increment = () => {
        const {value} = this.selectNumber
        const {count} = this.state
        this.setState({
            count: count +value
        })
    }
    decrement = () => {

    }
    incrementIfOdd = () => {

    }
    incrementAsync = () => {

    }

    render() {
        return (
            <div>
                <h1>当前求和为：0</h1>
                <select ref={c => this.selectNumber = c}>
                    <option value="1">1</option>
                    <option value="2">2</option>
                    <option value="3">3</option>
                </select>&nbsp;
                <button onChange={this.increment}>+</button>&nbsp;
                <button onChange={this.decrement}>-</button>&nbsp;
                <button onChange={this.incrementIfOdd}>当前求和为奇数再加</button>&nbsp;
                <button onChange={this.incrementAsync}>异步加</button>
            </div>
        )
    }
}
